/// <reference types="react" />

import * as React from "react";

interface HTMLAttributesWeak extends React.HTMLAttributes<HTMLElement> {
  title?: any;
}

export interface FeedbackProps extends HTMLAttributesWeak {
  /**
   * 样式类名的品牌前缀
   */
  prefix?: string;

  /**
   * 自定义类名
   */
  className?: string;

  /**
   * 自定义内联样式
   */
  style?: React.CSSProperties;

  /**
   * 反馈类型
   */
  type?: "success" | "error" | "prompt" | "help" | "loading";

  /**
   * 反馈外观
   */
  shape?: "inline" | "addon" | "toast";

  /**
   * 反馈大小
   */
  size?: "medium" | "large";

  /**
   * 标题
   */
  title?: React.ReactNode;

  /**
   * 内容
   */
  children?: React.ReactNode;

  /**
   * 是否显示
   */
  visible?: boolean;
}

export default class Feedback extends React.Component<FeedbackProps, any> {}
