interface HTMLAttributesWeak extends React.HTMLAttributes<HTMLElement> {
  onClick?: any;
  onSelect?: any;
}

export interface MenuProps extends HTMLAttributesWeak {
  /**
   * 样式类名的品牌前缀
   */
  prefix?: string;

  /**
   * 自定义类名
   */
  className?: string;

  /**
   * 自定义内联样式
   */
  style?: React.CSSProperties;

  /**
   * 点击菜单项触发的事件
   */
  onClick?: ((selectedKeys: Array<any>, menuItem: any, meta: {}) => void);

  /**
   * 当前选中的菜单项, 设置此属性，组件的选中变为受控状态
   */
  selectedKeys?: Array<any>;

  /**
   * 初始化选中的菜单项，只在组件初次render的时候生效
   */
  defaultSelectedKeys?: Array<any>;

  /**
   * 菜单选择的模式，支持单选和多选
   */
  selectMode?: "single" | "multiple";

  /**
   * 选中/取消选中了任意MenuItem
   */
  onSelect?: ((selectedKeys: Array<any>, menuItem: any, meta: {}) => void);

  /**
   * 取消选中的菜单项
   */
  onDeselect?: ((selectedKey: string) => void);

  /**
   * 如果此属性为true，表明只会选中第一级的菜单
   */
  shallowSelect?: boolean;

  /**
   * 当前打开的菜单项，设置此属性，组件的打开变为受控状态
   */
  openKeys?: Array<any>;

  /**
   * 初始化打开的菜单项，只在组件初次render的时候生效
   */
  defaultOpenKeys?: Array<any>;

  /**
   * 子菜单同时打开模式，是多个还是一个
   */
  openMode?: "single" | "multiple";

  /**
   * 打开子菜单的时候触发的事件
   */
  onOpen?: ((openKeys: Array<any>) => void);

  /**
   * 级联菜单下面缩进的尺寸
   */
  indentSize?: number;

  /**
   * 配置菜单的头部
   */
  header?: any;

  /**
   * 配置菜单的底部
   */
  footer?: any;

  /**
   * 是否启用多列模式
   */
  multipleCol?: boolean;

  /**
   * 是否启用设置焦点功能
   */
  autoFocus?: boolean;

  /**
   * 菜单的方向
   */
  direction?: "ver" | "hoz";
}

export default class Menu extends React.Component<MenuProps, any> {
  static Item: typeof Item;
  static SubMenu: typeof SubMenu;
  static Group: typeof Group;
  static Divider: typeof Divider;
  static PopupItem: typeof PopupItem;
  static RadioItem: typeof RadioItem;
  static CheckboxItem: typeof CheckboxItem;
}
