/// <reference types="react" />

import React from "react";

export interface FormBinderProps {
  /**
   * 全局校验滚动到报错位置时，距离顶部的偏移值（适用于头部 fixed 的场景）
   */
  scrollErrorFieldTopOffset?: number;

  /**
   * 全局校验时，是否开启滚动到报错表单位置
   */
  enableScrollErrorField?: any;

  /**
   * 当前表单的根 value 数据
   */
  value?: {};

  /**
   * 当前表单元素变更时触发
   */
  onChange?: (() => void);
}

export default class FormBinder extends React.Component<FormBinderProps, any> {}
